package basics;

public class SwapNumber {
	public static void main(String[] why) {
		// TODO Auto-generated method stub
     int a =10;
     int b = 20;
     System.out.println(a+" "+ b);
     int c;
     c=b;
     b=a;
     a=c;
     char c1 =(char)90;
     System.out.println(a+" "+ b+" "+c1);
     int t = 1;
     System.out.println(t++ + --t +"Hi"+ ++t + t--);
     }
}